<?php/** * Helper to Detect Admin Is Logged Or Not * @params Boolean AdminIsLogged * @params Array AdminLoginInfo - array of current user info * @return void * @author MohammadrezaEsmaeeli (info@mreweb.ir) * @license GPL * */$CI =& get_instance();$IsLogged = $CI->session->userdata('IsLogged');$LoginInfo = $CI->session->userdata('LoginInfo');if ($IsLogged == TRUE && count($LoginInfo) > 0) {}else {    redirect(base_url(''));}?>